    public void print () {
        try {
            Locale locale = new Locale( "id", "ID" );
            HashMap par = new HashMap();
            par.put(JRParameter.REPORT_LOCALE, locale);
            JasperPrint jp = JasperFillManager.fillReport
            (popUp_hasilPerhitungan.class.getResourceAsStream("/Report/report_hasilAkhir.jasper"), par, conn);
            JasperViewer.viewReport(jp, false);
        } catch (Exception ex) {
            JOptionPane.showMessageDialog(rootPane,"Dokumen Tidak Ada"+ex);
        }
    }     
    
    protected void TabelHasil(){
         Object [] Baris = {"NIS","Nama","ID Kriteria","Ujian Sekolah","Uji Kompetensi","Nilai Raport","Grade"};
            TabelHasil = new DefaultTableModel(null,Baris);
            String cariitem = txtcari.getText();
            try{
                String sql = "select tb_siswa.nis, tb_siswa.nama, tb_kriteria.id_kriteria, tb_kriteria.C1, tb_kriteria.C2, tb_kriteria.C3,"
                        + "tb_peringkat.grade from tb_siswa INNER JOIN tb_kriteria on tb_siswa.nis = tb_kriteria.nis "
                        + "INNER JOIN tb_peringkat on tb_kriteria.nis = tb_peringkat.nis where nama like '%"+cariitem+"%' or "
                        + "tb_siswa.nis like '%"+cariitem+"%' order by grade desc";

                Statement stat = conn.createStatement();
                ResultSet hasil = stat.executeQuery(sql);
                while (hasil.next()){
                    TabelHasil.addRow(new Object [] {
                        hasil.getString(1),
                        hasil.getString(2),
                        hasil.getString(3),
                        hasil.getFloat(4),
                        hasil.getFloat(5),
                        hasil.getFloat(6),
                        //hasil.getFloat(7),
                        //hasil.getFloat(8),
                        hasil.getFloat(7)
                        
                    });
                }
                tbhasil.setModel(TabelHasil);
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, "data gagal Dipanggil"+e);
            }
    }
    


