    private void bsaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsaveActionPerformed
        try {       
            for (int hasil = 0; hasil < TabelHasil.getRowCount(); hasil++){
                String sql1 = "insert into tb_hasil values(?,?,?,?,?,?,?)";
                PreparedStatement St1 = conn.prepareStatement(sql1);
                    St1.setString(1, (String)TabelHasil.getValueAt(hasil, 0));
                    St1.setString(2, (String)TabelHasil.getValueAt(hasil, 1));
                    St1.setString(3, (String)TabelHasil.getValueAt(hasil, 2));
                    St1.setFloat(4, (float)TabelHasil.getValueAt(hasil, 3));
                    St1.setFloat(5, (float)TabelHasil.getValueAt(hasil, 4));
                    St1.setFloat(6, (float)TabelHasil.getValueAt(hasil, 5));
                    St1.setFloat(7, (float)TabelHasil.getValueAt(hasil, 6));
                    //St1.setFloat(8, (float)TabelHasil.getValueAt(hasil, 7));
                    //St1.setFloat(9, (float)TabelHasil.getValueAt(hasil, 8));
                
                    St1.executeUpdate();
            }      
            JOptionPane.showMessageDialog(null, "data berhasil Disimpan");
        }catch (SQLException e){
            JOptionPane.showMessageDialog(null, "data gagal disimpan"+e);
         }
    }//GEN-LAST:event_bsaveActionPerformed

